"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[2575],{6980:(e,n,r)=>{r.r(n),r.d(n,{assets:()=>u,contentTitle:()=>a,default:()=>o,frontMatter:()=>i,metadata:()=>d,toc:()=>h});var t=r(4848),s=r(8453);const i={title:"1970 - Erstes Angeln",sidebar_position:6},a=void 0,d={id:"geschichte/erstes_angeln",title:"1970 - Erstes Angeln",description:"Erstmals Aus\xfcbung des Angelsports am vereinseigenen Gew\xe4sser",source:"@site/verein/geschichte/05_erstes_angeln.mdx",sourceDirName:"geschichte",slug:"/geschichte/erstes_angeln",permalink:"/asv-kirrberg/verein/geschichte/erstes_angeln",draft:!1,unlisted:!1,editUrl:"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/verein/geschichte/05_erstes_angeln.mdx",tags:[],version:"current",sidebarPosition:6,frontMatter:{title:"1970 - Erstes Angeln",sidebar_position:6},sidebar:"vereinSidebar",previous:{title:"1969 - Die ersten Feste",permalink:"/asv-kirrberg/verein/geschichte/ersten_feste"},next:{title:"1971 - Bau der Fischerb\xfctte",permalink:"/asv-kirrberg/verein/geschichte/bau_der_huette"}},u={},h=[];function g(e){const n={p:"p",...(0,s.R)(),...e.components};return(0,t.jsxs)(t.Fragment,{children:[(0,t.jsx)(n.p,{children:'Erstmals Aus\xfcbung des Angelsports am vereinseigenen Gew\xe4sser\nZur St\xe4rkung der Vereinskasse, sie hatte es dringend n\xf6tig, wurden auch heuer wieder\nzwei Weiherfeste abgehalten. Der Getr\xe4nkeausschank erfolgte aus einer nach zwei\nSeiten offenen H\xfctte, ,Ponte Rose" genannt, aus Holzst\xe4mmen errichtet. Trotz intensiver\nBem\xfchungen ging es mit der Genehmigung zum Bau der Fischerh\xfctte schleppend voran\nund mehrere Verhandlungen mit der Oberen und Unteren Naturschutzbeh\xf6rde sowie der\nForstbeh\xf6rde mu\xdften gef\xfchrt werden. Obwohl der Gemeinderat nichts gegen den\nH\xfcttenbau\neinzuwenden hatte, kam von der Baubeh\xf6rde gegen Ende des Jahres 1970\nstatt einer Baugenehmigung eine Ablehnung'}),"\n",(0,t.jsx)(n.p,{children:"Der Verein behielt die Ruhe und nun konnten sich die Mitglieder endlich so richtig dem\nAngelsport widmen. Erster Angeltag war der 12. April 1970 und schon wurden die ersten\nFangvorschriften erlassen. Der Fischbesatz bestand damals aus Wei\xdffischen, Karpfen\nund Schleien. Den ersten Besatz erbrachte f\xfcr den Verein kostenlos Mitglied Peter\nWentura, der seinen in Bechhofen angemieteten Weiher abfischte und s\xe4mtliche Fische\nin den neuen Weiher einsetzte. Hinzu kam noch der Ankauf von Karpfen und Schleien aus\ndem Karlsbergweiher. Nicht so reibungslos wie man es sich vorstellte, klappte es mit der\nBinhaltung der Anglervorschriften. Es gab anfangs manchen \xc4rger f\xfcr die Weiherwarte,\nweil einige Angler in der Anfangsphase die Bestimmungen nicht genau einhielten. Mit\ndem Angeln im Lambsbach wurde am Sonntag, 9. August, um 6 Uhr fr\xfch begonnen.\nIn der Generalversammlung am 6. Oktober wurde folgende Vorstandschaft gew\xe4hlt: 1.\nVorsitzender Andreas Sch\xfctz, 2. Vorsitzender Rudi Duppe, Schriftf\xfchrer Norbert Didion,\nKassenwart Willi Hofer, Unterkassierer Heiner Zott, Wasserwarte: Karl Hoffmann,\nEdmund Wentura und Hermann Sutter, Pressewart Markus Schanne, Beisitzer: Rudi\nKnecht, Heinz Bretzger, Horst Conrad, Hans Diehl und Konrad Lambert."}),"\n",(0,t.jsx)(n.p,{children:'1971\nBau der Fischerb\xfctte\nAnfang des Jahres war noch nicht entschieden, was aus dem Antrag des Vereins wird\nund ob der Bau einer H\xfctte genehmigt wird. Der Unterst\xfctzung des damaligen\nB\xfcrgermeisters Karl Diehl und des\nGemeindeamtmanns Rudi Didion war es mit zu\nverdanken, da\xdf durch die Baubeh\xf6rde nun doch \u201e,Gr\xfcnes Licht" gegeben und die\nBaugenehmigung f\xfcr eine Holzh\xfctte erteilt wurde.\nThema Nummer 1 im Verein war nun der H\xfcttenbau. Mitglied Karl Hoffmann fertigte ein\nModell an und einige Angebote \xfcber Fertigh\xe4user aus Holz wurden eingeholt. Inzwischen\ngenehmigte die Gemeinde die Verlegung der Versorgungsleitungen und mit Unterst\xfctzung\nder Firma Dahlhauser gelang dem Verein eine rasche Sanierung der Weiherd\xe4mme und\neine ebenso rasche Verlegung von Licht und Wasser\nSo sah es nach Erstellung der Anglerh\xfctte aus.'})]})}function o(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,t.jsx)(n,{...e,children:(0,t.jsx)(g,{...e})}):g(e)}},8453:(e,n,r)=>{r.d(n,{R:()=>a,x:()=>d});var t=r(6540);const s={},i=t.createContext(s);function a(e){const n=t.useContext(i);return t.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function d(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:a(e.components),t.createElement(i.Provider,{value:n},e.children)}}}]);